mapper.select:

select id, id_string as idString, gender, first_name as firstName, last_name as lastName, team, rank, role, 
		ptz_level as ptzLevel, auto_change_time as autoChangeTime, auto_handling_time as autoHandlingTime, block_until as blockUntil
from user_infos;


select id, first_name, last_name
  from user_infos
 where id=#{id }


select no, name, email, gender
  from user
 where no=#{no }


select no, name, email, gender
  from user
 where email=#{email }


select no, name, role
  from user
 where email=#{e }
   and password=#{p }

choose.when:

update user
   set name=#{name },	   
       password=#{password },
       gender=#{gender }
 where no=#{no }

choose.otherwise:

update user
   set name=#{name },
       gender=#{gender }
 where no=#{no }
